const MarqueeLine = () => {
  return (
    <div className="absolute top-1/2 left-0 w-full -translate-y-1/2 z-0 opacity-50 pointer-events-none">
      <div className="flex animate-marquee whitespace-nowrap">
        {[...technologies, ...technologies, ...technologies].map(
          (tech, index) => (
            <div
              key={index}
              className={`mx-8 text-6xl ${
                tech.name === "Next.js" && "dark:bg-white"
              } rounded-full`}
            >
              {tech.icon}
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default MarqueeLine;
